<script>
import { api_ordertotaldata } from "../../apis/apis";
import echarts from "echarts";
export default {
  created() {
    api_ordertotaldata().then((res) => {
      this.list = res.data;
    });
  },
  data() {
    return {
      list: {},
    };
  },
  mounted() {
    let Eobj = echarts.init(document.querySelector("#Myecharts"));
    Eobj.showLoading();

    setTimeout(() => {
      api_ordertotaldata().then((res) => {
        let option = {
          title: {
            text: "订单金额数据报表",
          },
          tooltip: {
            trigger: "axis",
          },
          legend: {
            data: ["金额数据", "订单数据"],
          },
          grid: {
            left: "3%",
            right: "4%",
            bottom: "3%",
            containLabel: true,
          },
          toolbox: {
            feature: {
              saveAsImage: {},
            },
          },
          xAxis: {
            type: "category",
            boundaryGap: false,
            data: res.data.xData,
          },
          yAxis: {
            type: "value",
          },
          // 控制有几条线
          series: [
            {
              name: "金额数据",
              type: "line",
              stack: "总量",
              data: res.data.amountData,
            },
            {
              name: "订单数据",
              type: "line",
              stack: "总量",
              data: res.data.orderData,
            },
          ],
        };

        Eobj.setOption(option);
         Eobj.hideLoading();
      });
    }, 1000);
   
  },

  methods: {},
};
</script>
    
